<?php\n// Path to the TTF font file\n$font = '/path/to/fontfile.ttf';\n\n// String to measure\n$string = 'Hello, world!';\n\n// Get the bounding box for this string with this font\n$bbox = imageftbbox(20, 0, $font, $string);\n\nif ($bbox === false) {\n    die('Error loading glyph. Check the font file and path.');\n}\n\n// Calculate dimensions of the text area\n$x1 = abs($bbox[0]);\n$y1 = abs($bbox[5]);\n$width = abs($bbox[2]) - abs($bbox[0]);\n$height = abs($bbox[7]) - abs($bbox[1]);\n\necho "Width: $width, Height: $height";\n?>