<?php\n\n// Your data to sign\n$data = "your_license_key_string_here";\n\n// Path to your private RSA key file\n$privateKeyFile = "/path/to/private/key.pem";\n\n// Read the private key from file\n$privateKey = openssl_pkey_get_private(\n    file_get_contents($privateKeyFile),\n    // If you have a passphrase for the private key, provide it here\n    "your_passphrase"\n);\n\nif ($privateKey === false) {\n    die("Failed to load private key");\n}\n\n// Initialize a signature variable\n$signature = "";\n\n// Sign the data with the private key using SHA256 as the hash algorithm\nif (openssl_sign($data, $signature, $privateKey, OPENSSL_ALGO_SHA256)) {\n    // Signature is in binary format; you might want to encode it for transmission or storage\n    $base64Signature = base64_encode($signature);\n    echo "Signature: " . $base64Signature;\n} else {\n    die("Failed to sign the data");\n}\n\n// Free the key resource\nopenssl_free_key($privateKey);\n\n?>