<?php\nfunction getUserAlternatingValue($userId) {\n    // Initialize the counter or retrieve it from memory\n    $key = "user_alternator_{$userId}";\n    $value = apcu_fetch($key, $success);\n\n    if (!$success) {\n        // If no entry exists for this user ID, initialize with 1 (or 0)\n        $value = 1;\n    }\n\n    // Store the toggled value back in memory\n    apcu_store($key, $value == 1 ? 0 : 1);\n\n    return $value;\n}\n\n// Example usage:\n$userId = session_id(); // Use a user-specific ID, such as session ID\n$result = getUserAlternatingValue($userId);\necho "User result: " . $result;\n\n?>