$cacheFile = 'tag_counts_cache.json';\n   // Time when cached file was written\n   $timeNow = time();\n   $refreshTime = 3600 * 24; // Once a day\n\n   if (file_exists($cacheFile) && ($timeNow - filemtime($cacheFile) < $refreshTime)) {\n       echo "Using cached data...\n";\n       $tagCounts = json_decode(file_get_contents($cacheFile), true);\n   } else {\n       echo "Querying database...\n";\n       $stmt = $database->query("SELECT t.tag_name, COUNT(vt.video_id) AS tag_count FROM tags t INNER JOIN videotags vt ON t.id = vt.tag_id GROUP BY t.id ORDER BY tag_count DESC");\n       $tagCounts = [];\n       foreach ($stmt as $row) {\n           $tagCounts[] = ['id' => $row['id'], 'name' => $row['tag_name'], 'count' => $row['tag_count']];\n       }\n       // Save cached data in JSON format\n       file_put_contents($cacheFile, json_encode($tagCounts));\n   }\n\n   foreach ($tagCounts as $tag) {\n       echo $tag["name"] . ', ';\n   }