<?php\n\n// URL of the page you want to scrape\n$url = 'http://example.com/page-with-links';\n\n// Fetch the content of the page\n$content = file_get_contents($url);\n\n// Create a new DOM Document and load HTML\n$doc = new DOMDocument();\n@$doc->loadHTML($content); // Suppress warnings with @\n\n// Find all links\n$links = $doc->getElementsByTagName('a');\n\n// Array to hold URLs\n$urlList = [];\n\n// Iterate over the list of nodes and extract their href attributes\nforeach ($links as $link) {\n    $url = $link->getAttribute('href');\n    if (!empty($url)) {\n        $urlList[] = $url;\n    }\n}\n\n// Write URLs to a text file, one per line\nfile_put_contents('urls.txt', implode("\n", $urlList));\n\necho "URLs have been written to urls.txt file.";\n?>