// Using IP address or hostname instead of socket\n$conn = mysqli_connect('127.0.0.1', 'username', 'password', 'database');\n\n// If you need to specify a socket, update the socket parameter accordingly\n$conn = mysqli_connect(\n    null,  // Hostname (NULL indicates default localhost)\n    'username',\n    'password',\n    'database',\n    null,       // Port number (NULL indicates default port)\n    '/path/to/mysql.sock'  // Socket file path\n);\n\nif (!$conn) {\n    die("Connection failed: " . mysqli_connect_error());\n}