// In your bootstrap.php or routes configuration file\n\nRoute::set('posts_index', 'posts')\n    ->defaults(array(\n        'controller' => 'Posts',\n        'action'     => 'index',\n    ))\n    ->bind('HTTP_METHOD', function () {\n        return Request::current()->method() === Request::GET;\n    });\n\nRoute::set('posts_create', 'posts')\n    ->defaults(array(\n        'controller' => 'Posts',\n        'action'     => 'create',\n    ))\n    ->bind('HTTP_METHOD', function () {\n        return Request::current()->method() == Request::POST;\n    });