public function setlabelAction()\n{\n    // Your existing logic here...\n    \n    $response = new stdClass();\n    if ($conditionToRedirect) {\n        $response->action = 'redirect';\n        $response->url = $this->view->url(['controller' => 'team', 'action' => 'manage', 'id' => $teamID]);\n    } else {\n        // Normal response\n        $response->action = 'update-label';\n        // Include any other data you need to send back for the AJAX update\n        $response->data = $this->view->render('myviewscript.ajax.phtml');\n    }\n\n    echo json_encode($response);\n    exit;\n}