require_once 'path/to/HTMLPurifier.auto.php';\n\n$config = HTMLPurifier_Config::createDefault();\n$config->set('Attr.EnableID', true); // Example: Enable ID if you want to allow it\n\n// Allow specific attributes on specific tags.\n$config->set('CSS.AllowedProperties', array()); // Set this empty to disallow all CSS properties\n$config->set('HTML.AllowedAttributes', 'a.href, img.src');\n\n$purifier = new HTMLPurifier($config);\n\n$dirtyHtml = '<p style="color: red;">Hello <b style="font-weight: bold;" id="test">world</b>!</p><a href="http://example.com" target="_blank">Example Link</a>';\n$cleanHtml = $purifier->purify($dirtyHtml);\n\necho $cleanHtml;