<?php\n   $inputGif = 'input.gif';\n   $outputGif = 'output_modified.gif';\n\n   // Create temporary directory for frames\n   $tempDir = sys_get_temp_dir() . '/frames';\n   mkdir($tempDir);\n\n   // Extract all frames into the temp directory\n   exec("convert {$inputGif} {$tempDir}/frame_%03d.png");\n\n   // Loop through frames and keep every 2nd frame (modify logic as needed)\n   $keepFrames = [];\n   foreach(glob("{$tempDir}/frame_*.png") as $i => $filename) {\n       if ($i % 2 === 0) { // Keep every second frame\n           array_push($keepFrames, basename($filename));\n       }\n   }\n\n   // Reconstruct GIF with selected frames\n   exec("convert " . implode(' ', array_map(function($frame) use ($tempDir) { return "{$tempDir}/{$frame}"; }, $keepFrames)) . " -loop 0 -delay 10 {$outputGif}");\n\n   // Clean up temporary files\n   array_map('unlink', glob("{$tempDir}/frame_*.png"));\n   rmdir($tempDir);\n   ?>