<?php\n    $how_many = 1000000;\n    $counter1 = 0;\n    $counter2 = 0;\n\n    $abc = array('boolean_lookup'=>TRUE, 'string_lookup'=>'something_else');\n\n    // Use microtime(true) for accurate timing in seconds as a float\n    $start = microtime(true);\n    for ($i = 0; $i < $how_many; $i++)\n    {\n        if ($abc['boolean_lookup'])\n        {\n            $counter1++;\n        }\n    }\n    \n    // Calculate the elapsed time\n    $elapsed_boolean = microtime(true) - $start;\n\n    echo "Boolean lookup took: " . $elapsed_boolean . " seconds\n";\n\n    echo '<hr>';\n\n    $start = microtime(true);\n    for ($i = 0; $i < $how_many; $i++)\n    {\n        if ($abc['string_lookup'] == 'something_else')\n        {\n            $counter2++;\n        }\n    }\n\n    // Calculate the elapsed time\n    $elapsed_string = microtime(true) - $start;\n\n    echo "String lookup took: " . $elapsed_string . " seconds\n";\n?>