<?php\n\nclass ServiceOne {\n    public function greet($name) {\n        return "Hello from Service One, $name!";\n    }\n}\n\nclass ServiceTwo {\n    public function greet($name) {\n        return "Hello from Service Two, $name!";\n    }\n}\n\n// Create a SoapServer instance without specifying a WSDL file\n$server = new SoapServer(null, array('uri' => 'http://example.com/soapservice'));\n\n// Define the functions that are available via SOAP. \n// You can map them to different class methods.\nfunction serviceOneGreet($name) {\n    $serviceOne = new ServiceOne();\n    return $serviceOne->greet($name);\n}\n\nfunction serviceTwoGreet($name) {\n    $serviceTwo = new ServiceTwo();\n    return $serviceTwo->greet($name);\n}\n\n// Attach the functions to SOAP methods\n$server->addFunction('serviceOneGreet');\n$server->addFunction('serviceTwoGreet');\n\n// Handle incoming requests\n$server->handle();\n\n?>