$s = new SimpleXMLElement('<root/>');\n$t = new SimpleXMLElement('<child/>');\n\n// Convert $s to DOMDocument\n$domS = dom_import_simplexml($s);\n$domT = dom_import_simplexml($t);\n\n// Import '$domT' into the document context of '$domS'\n$importedNode = $domS->ownerDocument->importNode($domT, true);\n\n// Append the imported node to '$domS'\n$domS->appendChild($importedNode);\n\n// Convert back to SimpleXMLElement\n$sNew = simplexml_import_dom($domS);\n\necho $sNew->asXML();