<?php\n// List of common bots' user-agents\n$bots = [\n    'Googlebot', // Google\n    'Slurp',     // Yahoo\n    'bingbot',   // Bing\n    'Baiduspider', // Baidu\n    'YandexBot',\n    'DuckDuckBot',\n    // Add more bots as needed\n];\n\n// Get the user-agent string from the server request\n$userAgent = $_SERVER['HTTP_USER_AGENT'];\n\n// Check if any bot's User-Agent is in the current request's User-Agent string\n$isBot = false;\nforeach ($bots as $bot) {\n    if (stripos($userAgent, $bot) !== false) {\n        $isBot = true;\n        break;\n    }\n}\n\nif (!$isBot) {\n    // Increment visit count only if the visitor is not a bot\n    // Example: Assuming you have a function to increment your MySQL counter\n    incrementVisitCount();\n}\n\nfunction incrementVisitCount() {\n    // Your existing logic for increasing the visit count in the database goes here\n}\n?>