/**\n * @package MyExceptions\n * MyWrongTypeException occurs when an object or data structure is of the incorrect datatype.\n * Program defensively!\n */\nclass MyWrongTypeException extends RuntimeException {\n    public function __construct($objectName, $object, $expected, $message = '', $code = 0, Throwable $previous = null) {\n        $receivedType = gettype($object);\n        // Construct a meaningful message\n        $messageTemplate = "Wrong Type: %s. Expected: %s, Received: %s.";\n        $userMessage = sprintf($messageTemplate, $objectName, $expected, $receivedType);\n\n        // Append additional custom message if provided\n        if (!empty($message)) {\n            $userMessage .= " Additional Info: $message";\n        }\n\n        // Optionally append debug information\n        if (App::get_mode() == 'debug') {\n            ob_start();\n            var_dump($object);\n            $userMessage .= "\nDebug Info:\n" . ob_get_clean();\n        }\n\n        parent::__construct($userMessage, $code, $previous);\n    }\n}