function safeFileGetContents($url) {\n    // Validate URL\n    if (!filter_var($url, FILTER_VALIDATE_URL)) {\n        throw new InvalidArgumentException('Invalid URL provided.');\n    }\n\n    // Set a timeout for the request\n    $context = stream_context_create([\n        'http' => [\n            'timeout' => 5, // Timeout in seconds\n        ],\n    ]);\n\n    set_error_handler(function($severity, $message) use ($url) {\n        throw new Exception("Failed to fetch content from {$url}: {$message}", $severity);\n    }, E_WARNING);\n\n    try {\n        // Fetch the contents of the URL\n        $content = file_get_contents($url, false, $context);\n        restore_error_handler();\n        return $content;\n    } catch (Exception $e) {\n        restore_error_handler();\n        throw new Exception($e->getMessage(), $e->getCode());\n    }\n}\n\ntry {\n    $webContent = safeFileGetContents('http://yourwebsite.com/page');\n    // Search through the content\n    if (strpos($webContent, 'search_text') !== false) {\n        echo "Text found in the web page.";\n    } else {\n        echo "Text not found.";\n    }\n} catch (Exception $e) {\n    echo "Error: " . $e->getMessage();\n}