function exampleFunction() {\n    $vars = [];\n    $user_name = 'John Doe';\n    $user_age = 30;\n    $is_active = true;\n\n    // Manually add variables to the array\n    $vars['user_name'] = $user_name;\n    $vars['user_age'] = $user_age;\n    $vars['is_active'] = $is_active;\n\n    echo "<pre>";\n    foreach ($vars as $varname => $value) {\n        print "$varname = " . print_r($value, true) . "\n";\n    }\n    echo "</pre>";\n}\n\nexampleFunction();