<?php\n$host = 'localhost';\n$dbname = 'your_database_name';\n$username = 'your_username';\n$password = 'your_password';\n\n$conn = new mysqli($host, $username, $password, $dbname);\n\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Assuming $searchTerm is the variable passed from JavaScript\n$searchTerm = $_POST['query'];\n\nif (!$searchTerm) {\n    echo json_encode([]);\n    exit;\n}\n\n$stmt = $conn->prepare("SELECT c.name AS CategoryName, p.name AS ProductName FROM category_products cp JOIN categories c ON cp.category_id = c.id JOIN products p ON cp.product_id = p.id WHERE c.name LIKE ?");\n$searchTerm = "%$searchTerm%";\n$stmt->bind_param("s", $searchTerm);\n$stmt->execute();\n$result = $stmt->get_result();\n\n$data = [];\nwhile ($row = $result->fetch_assoc()) {\n    $data[] = $row;\n}\n\necho json_encode($data);\n\n$stmt->close();\n$conn->close();\n?>