$author_array = [8, 1, 6];\n\n$user_results = [\n    (object) ['ID' => 1, 'user_login' => 'user1'],\n    (object) ['ID' => 6, 'user_login' => 'user6'],\n    (object) ['ID' => 8, 'user_login' => 'user8']\n];\n\n// Create a mapping of user IDs to their index in $author_array\n$idToIndex = array_flip($author_array);\n\n// Sort $user_results based on the order defined in $author_array\nusort($user_results, function($a, $b) use ($idToIndex) {\n    return $idToIndex[$a->ID] <=> $idToIndex[$b->ID];\n});\n\nprint_r($user_results);