function browse() {\n    $categories = new Categories();\n    $categories_list = $categories->build_list();\n\n    $all_categories = [];\n    foreach ($categories_list as $c) {\n        // Assuming you want to store the category name and ID, adjust as necessary\n        $all_categories[] = [\n            'id' => $c['id'],\n            'name' => $c['c_name']\n        ];\n    }\n\n    return $all_categories;\n}