require_once('msn.class.php');\n\nclass MyMsnBot extends Msn {\n    public function __construct() {\n        parent::__construct();\n        $this->setEventCallback("onContactFriendRequest", array($this, "handleFriendRequest"));\n    }\n\n    public function handleFriendRequest($contact, $message) {\n        // Accept the friend request\n        $this->addContact($contact);\n        echo "Accepted friend request from: " . $contact->getFriendlyName() . "\n";\n    }\n}\n\n$bot = new MyMsnBot();\n$bot->login("your_email@hotmail.com", "your_password");