function dumpVariables() {\n    // Merge different superglobals\n    $all_vars = array_merge(\n        $_GET,\n        $_POST,\n        $_COOKIE,\n        $_SESSION, // Make sure session is started if you want this one\n        $_SERVER,\n        $_FILES,\n        $_ENV\n    );\n\n    // If you have other arrays of variables that you want to include, add them here\n    // $all_vars = array_merge($all_vars, $yourCustomArray);\n\n    echo "<pre>";\n    foreach ($all_vars as $varname => $value) {\n        print "$varname = " . print_r($value, true) . "\n";\n    }\n    echo "</pre>";\n}\n\n// Start session if not already started\nif (session_status() == PHP_SESSION_NONE) {\n    session_start();\n}\n\n// Call the function to dump variables\ndumpVariables();