// Load libraries and helpers in your controller's constructor or specific method\n$this->load->library('form_validation');\n$this->load->helper('security');\n\nfunction submit_textarea() {\n    // Set rules for form validation\n    $this->form_validation->set_rules('user_text', 'User Text', 'required|min_length[10]|max_length[400]');\n\n    if ($this->form_validation->run() == FALSE) {\n        // Validation failed, show form with error messages\n        $this->load->view('your_form_view');\n    } else {\n        // Get the user input and sanitize it\n        $user_text = strip_tags($this->input->post('user_text'), '<p><b><i>'); // Only allow paragraphs, bold, and italic tags\n\n        // Save sanitized data to the database (assuming you have a model called 'Text_model')\n        $this->load->model('Text_model');\n        $this->Text_model->save_user_text($user_text);\n\n        // Redirect or load another view to indicate success\n        redirect('your/success/url');\n    }\n}\n\nfunction display_textarea() {\n    // Load data from the database and escape it for HTML output\n    $this->load->model('Text_model');\n    $data['user_text'] = html_escape($this->Text_model->get_user_text());\n\n    $this->load->view('your_display_view', $data);\n}