<?php\n// Create a 200x100 true color image\n$image = imagecreatetruecolor(200, 100);\n\n// Define a background color (e.g., white)\n$backgroundColor = imagecolorallocate($image, 255, 255, 255);\nimagefill($image, 0, 0, $backgroundColor);\n\n// Define a semi-transparent blue color\n$transparentColor = imagecolorallocatealpha($image, 64, 128, 255, 64); // Last parameter is the alpha level (0-127)\n\n// Draw a rectangle with the transparent color\nimagefilledrectangle($image, 30, 30, 170, 70, $transparentColor);\n\n// Make sure to disable blending and set the savealpha flag for true color images\nimagesavealpha($image, true);\nimagealphablending($image, false);\n\n// Output the image to a browser (could also use file path or other outputs)\nheader('Content-Type: image/png');\nimagepng($image);\n\n// Free up memory\nimagedestroy($image);\n?>