$split_point = ' - ';\n$string = 'this is my - string - and more';\n\n// Find the position of the last occurrence of the delimiter\n$lastPos = strrpos($string, $split_point);\n\nif ($lastPos !== false) {\n    // Split the string into two parts at the last occurrence of the delimiter\n    $item[0] = substr($string, 0, $lastPos);\n    $item[1] = substr($string, $lastPos + strlen($split_point));\n}\n\nprint_r($item);