// Example SOAP call using PHP to create a Lead with set_entry()\n$session = 'your_session_id'; // Obtain this from login()\n$module_name = 'Leads';\n$name_value_list = array(\n    array(\n        'name' => 'first_name',\n        'value' => 'John'\n    ),\n    array(\n        'name' => 'last_name',\n        'value' => 'Doe'\n    ),\n    array(\n        'name' => 'email1',\n        'value' => 'john.doe@example.com'\n    )\n);\n\n$args = array(\n    'session' => $session,\n    'module_name' => $module_name,\n    'name_value_list' => $name_value_list\n);\n\n$result = $client->set_entry($args);