<?php \ninclude "header.php";\ninclude "wrap.php";\ninclude "left.php";\n?>\n\n<div id="content">\n    <div id="ad728x90">\n        <?php include "ad728x90.php";?>\n    </div>\n    <?php include "utilplaces.php";?>\n    <h1>Places by Name</h1>\n    <div id="horizon">\n        <a href="?bbname=a">A</a> \n        <a href="?bbname=b">B</a> \n        <!-- Add more links as needed -->\n        <a href="?bbname=0">0-9</a>\n    </div>\n    <div class="cnp"></div>\n</div>\n\n<div id="biggun">\n<?php \n// Connects to the Database using mysqli\n$conn = mysqli_connect("localhost", "user", "password", "databasename") or die(mysqli_error($conn));\n\n// Initialize $bbname variable\n$bbname = isset($_GET['bbname']) ? $_GET['bbname'] : '';\n\n// Prepare your SQL statement to prevent SQL injection\n$sql = "SELECT * FROM places";\nif (!empty($bbname)) {\n    $sql .= " WHERE name LIKE ?";\n}\n\n$sql .= " ORDER BY name ASC LIMIT 0, 30";\n\n// Use prepared statements to safely execute the query\n$stmt = mysqli_prepare($conn, $sql);\n\nif (!empty($bbname)) {\n    // Bind parameters for markers ( ? ) in SQL statement\n    $bbnameParam = '%' . $bbname . '%';\n    mysqli_stmt_bind_param($stmt, "s", $bbnameParam);\n}\n\n// Execute the query\nmysqli_execute($stmt);\n\n// Get result\n$result = mysqli_stmt_get_result($stmt);\n\necho "<table id=\"placesstable\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\">";\necho "    <col id=\"bbNAME\" />";\necho "    <col id=\"bbIMG\" />";\necho "    <col id=\"bbDETAILS\" />";\necho "    <col id=\"bbHOURS\" />";\necho "    <col id=\"bbTYPE\" />";\necho "    <col id=\"bplacesEA\" />";\necho "    <col id=\"bbRATING\" />";\necho "    <thead>";\necho "      <tr>";\necho "        <th style=\"border: none;\">Name</th>";\necho "        <th>&nbsp;</th>";\necho "        <th>Details </th>";\necho "        <th>Hours</th>";\necho "        <th>Type</th>";\necho "        <th>Area</th>";\necho "        <th> Rating</th>";\necho "      </tr>";\necho "    </thead>";\necho "    <tbody>";\n\nwhile ($info = mysqli_fetch_array($result)) {\n    echo "<tr>"; \n    echo "<td><p class=\"placesnametable\"><a href=\"#\">".$info['name'] . "</a></p></td> ";\n    echo "<td><img src=\"".$info['tmbimg']."\" alt=\"\" name=\"placesthumb\" width=\"100\" height=\"67\" class=\"placesthumb\" /></td> ";\n    echo "<td>".$info['address'] . "<br />\n" .$info['phonenumber'] . "</td>"; \n    echo "<td>".$info['hours'] . "</td> "; \n    echo "<td>".$info['type'] . "</td> "; \n    echo "<td>".$info['district'] . "</td> "; \n    echo "<td>Pending<!--RATING--></td></tr>"; \n}\n\necho "</tbody>";\necho "</table>";\n\n// Close statement and connection\nmysqli_stmt_close($stmt);\nmysqli_close($conn);\n\n?>\n</div>\n<?php include "feetie.php";?>