// Start session at the beginning of each page/file\nsession_start();\n\nclass User {\n    public $isLoggedIn = false;\n    // Define other properties as necessary\n    \n    public function login($username, $password) {\n        // Logic to check username and password against database\n        if ($this->authenticateUser($username, $password)) {\n            $this->isLoggedIn = true;\n            $_SESSION['user'] = serialize($this); // Store serialized object in session\n        }\n    }\n\n    private function authenticateUser($username, $password) {\n        // Logic to check credentials\n        return $username === 'admin' && $password === 'password'; // Dummy check\n    }\n}\n\n// Check if user is already logged in\nif (isset($_SESSION['user'])) {\n    $user = unserialize($_SESSION['user']);\n} else {\n    $user = new User();\n}\n\nif ($_SERVER['REQUEST_METHOD'] === 'POST') {\n    // Example login attempt\n    $user->login($_POST['username'], $_POST['password']);\n    // Redirect or handle as needed\n}