// In your controller method\n\n// Render the view part to get just the body content\n$view = new View($this, false);\n$bodyContent = $view->render('your_view_file', 'ajax'); // Use a view or 'default' as needed\n\n// Render the layout with the body content injected\n$layout = $view->element('email/html/default.ctp', array('content_for_layout' => $bodyContent));\n\n// Now you have the full HTML including headers and footers in $layout\n\nheader('Content-Type: text/html');\necho $layout;\nexit;