<?php\nsession_start();\n\n// Check if form was submitted\nif ($_SERVER["REQUEST_METHOD"] == "POST") {\n    // Store search preferences in session variables\n    $_SESSION['search_preferences'] = $_POST;\n}\n\n// Access the stored search preferences on any page after starting the session with session_start()\n$searchPreferences = isset($_SESSION['search_preferences']) ? $_SESSION['search_preferences'] : [];\n\n// Example usage\nif (!empty($searchPreferences)) {\n    // Process the stored search preferences\n    echo "Previous Search Term: " . htmlspecialchars($searchPreferences['search_term']);\n}\n?>