<?php\n$query = "SELECT * FROM your_table";\n$result = mysqli_query($conn, $query);\n\nwhile ($row = mysqli_fetch_assoc($result)) {\n    // Count the words in the field\n    $wordCount = str_word_count(trim($row['your_field']));\n    \n    if ($wordCount < 4) {\n        // Process this row since it has less than 4 words\n        echo "Row with less than 4 words: " . $row['your_field'] . "\n";\n        \n        // Optionally, break after the first match\n        // break;\n    }\n}\n\nmysqli_free_result($result);\n?>