$xmlString = <<<XML\n<root>\n    <element1 page="1">blah</element1>\n    <element2 page="1">blah blah</element2>\n    <element3 page="2">blah</element3>\n    <element4 page="3">blah blah</element4>\n</root>\nXML;\n\n$xml = simplexml_load_string($xmlString);\n\n// Perform the XPath query\n$nodes = $xml->xpath('//node()[@page="1"]');\n\n$resultArray = [];\nforeach ($nodes as $node) {\n    // Use (string)$node to get the text content and $node->getName() for node name\n    $resultArray[$node->getName()] = (string)$node;\n}\n\nprint_r($resultArray);