// Array of bot keywords\n$botKeywords = [\n    'bot', 'spider', 'crawl', 'slurp', 'yandex', 'googlebot', 'bingbot',\n    'duckduckbot', 'baiduspider', 'sogou', 'exabot', 'facebot', 'ia_archiver'\n];\n\n$count = 0; // Initialize counter\n\nwhile($row = mysql_fetch_array($yesterday, MYSQL_ASSOC)) {\n    $userAgent = strtolower($row['user_agent']); // Convert to lowercase for case-insensitive comparison\n    $isBot = false;\n\n    foreach ($botKeywords as $keyword) {\n        if (strpos($userAgent, $keyword) !== false) {\n            $isBot = true;\n            break; // No need to check further if a keyword is found\n        }\n    }\n\n    if (!$isBot) {\n        $count++;\n    }\n}