<?php\n// Assuming this script generates the image and sends it to the client\nheader("Content-Type: image/png"); // or image/jpeg depending on your image type\n\n// Disable caching\nheader('Cache-Control: no-store, no-cache, must-revalidate'); // HTTP/1.1\nheader('Cache-Control: post-check=0, pre-check=0', false);\nheader('Pragma: no-cache'); // HTTP/1.0\nheader('Expires: 0');\n\n// Your code to generate the image would go here...\n// For example:\n$image = imagecreate(120, 20);\n$black = imagecolorallocate($image, 0, 0, 0);\n$white = imagecolorallocate($image, 255, 255, 255);\n\nimagestring($image, 3, 5, 5, 'A Simple Text String', $white);\nimagepng($image);\n\nimagedestroy($image);\n?>