function transliterate_to_ascii($string) {\n    // Transliteration table for specific characters\n    $transliterated = strtr(\n        utf8_decode($string),\n        array('à' => 'a', 'ä' => 'ae', 'é' => 'e', 'ë' => 'e',\n              'ï' => 'i', 'ö' => 'oe', 'ü' => 'ue', 'ß' => 'ss')\n    );\n\n    // Remove any remaining special characters\n    $transliterated = preg_replace('/[^a-z0-9]/i', '-', strtolower($transliterated));\n\n    // Reduce multiple hyphens to a single one\n    $transliterated = preg_replace('/-+/', '-', trim($transliterated, '-'));\n\n    return $transliterated;\n}\n\n$title = "Aït Ben Haddou";\n$url_slug = transliterate_to_ascii($title);\necho "/gallery/280-" . $url_slug . "/";