<?php\n$html = '<table>\n   <tr>\n     <td>quote1</td>\n     <td>have you trying it off and on again ?</td>\n   </tr>\n   <tr>\n     <td>quote65</td>\n     <td>You wouldn\'t steal a helmet of a policeman</td>\n   </tr>\n</table>';\n\n$pattern = '#<tr>\s*<td>(.*?)</td>\s*<td>(.*?)</td>\s*</tr>#';\npreg_match_all($pattern, $html, $matches);\n\nforeach ($matches[1] as $index => $quote) {\n    echo "$quote:{$matches[2][$index]}\n";\n}\n?>