<?php\n\n// Assuming $data is your array containing parsed XML data\n$data = [\n    ['column1' => 'value1', 'column2' => 'value2'],\n    // ... more rows ...\n];\n\n$pdo = new PDO('mysql:host=localhost;dbname=yourDatabase', 'username', 'password');\n$pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n// Define your batch size\n$batchSize = 100;\n$totalRecords = count($data);\n$batches = ceil($totalRecords / $batchSize);\n\nfor ($i = 0; $i < $batches; $i++) {\n    $start = $i * $batchSize;\n    $end = min(($i + 1) * $batchSize, $totalRecords);\n    \n    // Prepare a batch of records\n    $valuesBatch = [];\n    for ($j = $start; $j < $end; $j++) {\n        $valuesBatch[] = '(' . implode(',', [\n            "'" . $pdo->quote($data[$j]['column1']) . "'",\n            "'" . $pdo->quote($data[$j]['column2']) . "'"\n        ]) . ')';\n    }\n    \n    // Create an SQL insert query for the batch\n    $sql = "INSERT INTO your_table (column1, column2) VALUES " . implode(',', $valuesBatch);\n    \n    // Execute the query\n    $pdo->exec($sql);\n}\n\n?>