<?php\n\nfunction sendErrorEmail($errorMessage) {\n    $to = 'your-email@example.com';\n    $subject = 'PHP Error Notification';\n    mail($to, $subject, $errorMessage);\n}\n\nfunction errorHandler($errno, $errstr, $errfile, $errline) {\n    // Format the error message\n    $errorMessage = "[$errno] $errstr - Error on line $errline in file $errfile";\n    \n    // Log the error to a file or database\n    error_log($errorMessage);\n    \n    // Send an email notification\n    sendErrorEmail($errorMessage);\n\n    // Optionally, you can return true false to allow PHP's default error handler run\n    return false;\n}\n\n// Set the custom error handler\nset_error_handler("errorHandler");\n\n// Example of a line that will trigger an error\necho $undefinedVariable;\n\n?>