<?php\n// Database connection details (change these according to your settings)\n$servername = "localhost";\n$username = "username";\n$password = "password";\n$dbname = "dbname";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\nif ($_SERVER["REQUEST_METHOD"] == "POST") {\n    // Get the input dates and sanitize them\n    $input_sdate = $_POST['sdate'];\n    $input_edate = $_POST['edate'];\n\n    // Convert user input to MySQL date format (YYYY-MM-DD)\n    $sdate = date('Y-m-d', strtotime($input_sdate));\n    $edate = date('Y-m-d', strtotime($input_edate));\n\n    // Prepare the SQL statement\n    $stmt = $conn->prepare("SELECT sdate, `Project Name`, edate FROM projects WHERE sdate BETWEEN ? AND ? OR edate BETWEEN ? AND ?");\n    \n    // Bind parameters and execute the query\n    $stmt->bind_param("ssss", $sdate, $edate, $sdate, $edate);\n    $stmt->execute();\n\n    // Get the result\n    $result = $stmt->get_result();\n    \n    // Check if there are any results\n    if ($result->num_rows > 0) {\n        echo "<table border='1'><tr><th>sdate</th><th>Project Name</th><th>edate</th></tr>";\n        // Output data of each row\n        while($row = $result->fetch_assoc()) {\n            echo "<tr><td>" . $row["sdate"] . "</td><td>" . $row["Project Name"] . "</td><td>" . $row["edate"] . "</td></tr>";\n        }\n        echo "</table>";\n    } else {\n        echo "0 results";\n    }\n\n    // Close the statement\n    $stmt->close();\n}\n\n// Close the connection\n$conn->close();\n?>