<?php\n$inputFile = 'input.md'; // Markdown file\n$outputFile = 'output.html'; // HTML output file\n\n// Write markdown content to input file\n$markdownContent = "# Hello World\nThis is a test.";\nfile_put_contents($inputFile, $markdownContent);\n\n// Execute Node.js script\nshell_exec('node convertMarkdown.js ' . escapeshellarg($inputFile) . ' ' . escapeshellarg($outputFile));\n\n// Read the HTML content from output file\n$htmlOutput = file_get_contents($outputFile);\necho htmlspecialchars($htmlOutput); // Output HTML safely\n\n// Optionally, store $markdownContent and $htmlOutput in your database\n?>