<?php\n$url = 'http://example.com/image.php';\n$imageData = file_get_contents($url);\n\nif ($imageData === FALSE) {\n    die('Error downloading the image.');\n}\n\n$filepath = '/path/to/save/flower.jpg'; // Specify the path and filename\n\n$fileHandle = fopen($filepath, 'w');\nif ($fileHandle === FALSE) {\n    die('Error opening the file for writing.');\n}\n\nfwrite($fileHandle, $imageData);\nfclose($fileHandle);\n\necho "Image saved successfully.";\n?>