function random_float ($min,$max) {\n   return ($min+lcg_value()*(abs($max-$min)));\n}\n\n$floatArray = [];\nfor ($i = 0; $i < 10; $i++) {\n    $randomFloat = random_float(6, 8);\n    $roundedFloat = round($randomFloat, 1);\n    // Format the float to one decimal place\n    $formattedFloat = sprintf("%.1f", $roundedFloat);\n    $floatArray[] = (float)$formattedFloat; // Convert back to float for array\n}\n\necho serialize($floatArray);