<?php \ninclude 'values.php'; // Include values.php to get the savings amounts\n\n$monthlybill = isset($_GET['monthlybill']) ? (float)$_GET['monthlybill'] : 0; \n\nif ($monthlybill <= 0) {\n    echo "Please enter a valid monthly bill amount.";\n} else {\n\necho "<p>Your monthly bill was $monthlybill</p>\n<table border='1'>\n<tr>\n<th>Service Logo</th>\n<th>Rating & Actions</th>\n<th>Calculated Savings</th>\n</tr>";\n\n$re = 1;\nwhile ($re <= 7) {\n    $offername = 'offer' . $re . 'name';\n    $offerlogo = $offername ? ${'offer' . $re . 'logo'} : '';\n    $offerlink = $offername ? ${'offer' . $re . 'link'} : '#';\n    $offercalcsavings = isset(${'offer' . $re . 'calcsavings'}) ? (float)${'offer' . $re . 'calcsavings'} : 0;\n    \n    if ($offername) {\n        // Calculate savings\n        $savings = $monthlybill - $offercalcsavings;\n\n        echo "<tr>\n            <td><a href=\"$offerlink\" target=\"_blank\">\n                <img src=\"http://www.nextadvisor.com$offerlogo\" alt=\"$offername\" />\n              </a></td>\n            <td>\n                <span class=\"rating_text\">Rating:</span>\n                <span class=\"star_rating1\">\n                    <img src=\"http://www.nextadvisor.com" . ${'offer' . $re . 'star1'} . "\" alt=\"\" />\n                </span><br />\n                <div style='margin-top:5px; color:#0000FF;'>\n                    <a href=\"$offerlink\" target=\"_blank\">Go to Site</a>\n                    <span style='margin:0px 7px 0px 7px;'>|</span>\n                    <a href=\"" . ${'offer' . $re . 'anchor'} . "\">Review</a>\n                </div>\n            </td>\n            <td>\$" . number_format($savings, 2) . "</td>\n        </tr>";\n    }\n    \n    $re++;\n}\n\necho "</table>";\n}\n?>