class Model_User extends Model_Person {\n    protected static $_userGateway;\n\n    public static function init() {\n        parent::init();\n        if (self::$_userGateway == null) {\n            self::$_userGateway = new Model_Table_User();\n        }\n    }\n\n    // Override the login method or any other user-specific methods\n    public function login($userName, $password) {\n        $gateway = self::$_userGateway;\n        $whereUser = $gateway->getAdapter()->quoteInto('userName = ?', $userName);\n        $wherePass = $gateway->getAdapter()->quoteInto('password = ?', md5($password)); // Note: MD5 is not secure; use bcrypt or similar\n        $rowCount = $gateway->fetchRow([$whereUser, $wherePass]);\n        \n        return !is_null($rowCount); // Return true if user exists and password matches\n    }\n\n    public function setPassword($newPassword) {\n        $data = ['password' => md5($newPassword)]; \n        self::$_userGateway->update($data, 'person_id = ' . (int)$this->_person->person_id);\n    }\n}