$ch = curl_init();\n\n// Set the URL\ncurl_setopt($ch, CURLOPT_URL, "https://secure.paymentgateway.com/blah.php");\n\n// Set the user agent\ncurl_setopt($ch, CURLOPT_USERAGENT, "Mozilla/4.0 (compatible; MSIE 5.01; Windows NT 5.0)");\n\n// Return the transfer as a string instead of outputting it directly\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n\n// Enable SSL verification to ensure security\ncurl_setopt($ch, CURLOPT_SSL_VERIFYPEER, true);\ncurl_setopt($ch, CURLOPT_SSL_VERIFYHOST, 2);\n\n// Set the path to your CA bundle if necessary\n$caBundlePath = getcwd() . '/cert/ca.crt';\nif (file_exists($caBundlePath)) {\n    curl_setopt($ch, CURLOPT_CAINFO, $caBundlePath);\n}\n\n// If you need to use a client certificate, uncomment and adjust these lines:\n// $clientCertPath = getcwd() . '/cert/mycert.pem';\n// if (file_exists($clientCertPath)) {\n//     curl_setopt($ch, CURLOPT_SSLCERT, $clientCertPath);\n// }\n//\n// // If your client certificate is password protected, provide the password\n// curl_setopt($ch, CURLOPT_SSLCERTPASSWD, 'password');\n\n// Set up POST request\ncurl_setopt($ch, CURLOPT_POST, true);\ncurl_setopt($ch, CURLOPT_POSTFIELDS, "variables...");\n\n$output = curl_exec($ch);\n\nif ($output === false) {\n    echo 'Curl error: ' . curl_error($ch);\n} else {\n    echo $output;\n}\n\ncurl_close($ch);