<?php\n// task.php\n\n$imagePath = 'path/to/image.jpg';\n$targetSizes = ['thumbnail', 'medium', 'large'];\n\n// Example database insert for simplicity\n$conn = new PDO('mysql:host=localhost;dbname=testdb', 'user', 'password');\n$stmt = $conn->prepare("INSERT INTO tasks (image_path, sizes) VALUES (:path, :sizes)");\n$stmt->execute([':path' => $imagePath, ':sizes' => implode(',', $targetSizes)]);