<?php\nfunction followsRedirect($url) {\n    $ch = curl_init();\n    curl_setopt($ch, CURLOPT_URL, $url);\n    curl_setopt($ch, CURLOPT_HEADER, true);    // We want the header\n    curl_setopt($ch, CURLOPT_NOBODY, true); // Don't need the body\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);   // Return as a string instead of outputting directly\n    curl_setopt($ch, CURLOPT_FOLLOWLOCATION, false); // Don't follow redirects (important for detecting)\n\n    $response = curl_exec($ch);\n    curl_close($ch);\n\n    if ($response !== false) {\n        $http_code = curl_getinfo($ch, CURLINFO_HTTP_CODE);\n        return in_array($http_code, array(301, 302, 303, 307, 308));\n    }\n\n    return false;\n}\n\n$url = "http://example.com"; // replace with your URL\nif (followsRedirect($url)) {\n    echo "The URL redirects.";\n} else {\n    echo "The URL does not redirect.";\n}\n?>