$query = 'SELECT views FROM post ORDER BY views ASC LIMIT 31'; // Ensure at least 31 results are fetched\n$result = mysql_query($query);\n\nif ($result && mysql_num_rows($result) > 30) {\n    mysql_data_seek($result, 30);\n    $useableResult = mysql_fetch_row($result);\n    echo $useableResult[0] . '<br/>'; // Access the first column of the row\n} else {\n    echo "Not enough rows to fetch index 30.";\n}