<?php\n\n$filename = 'db_names.txt'; // The file containing the list of databases\n$mysqli = new mysqli("localhost", "username", "password");\n\nif ($mysqli->connect_error) {\n    die('Connect Error (' . $mysqli->connect_errno . ') '\n            . $mysqli->connect_error);\n}\n\n$file_handle = fopen($filename, "r");\nwhile (!feof($file_handle)) {\n    $db_name = trim(fgets($file_handle));\n    if (strpos($db_name, 'Whatever_') === 0) { // Check if the database name starts with 'Whatever_'\n        $drop_query = "DROP DATABASE IF EXISTS `" . $mysqli->real_escape_string($db_name) . "`";\n        if ($mysqli->query($drop_query) === TRUE) {\n            echo "Dropped database: " . $db_name . "\n";\n        } else {\n            echo "Error dropping database " . $db_name . ": " . $mysqli->error . "\n";\n        }\n    }\n}\nfclose($file_handle);\n\n$mysqli->close();\n\n?>