header('Content-Type: application/pdf');\nheader('Content-Length: ' . strlen($pdfData));\n// The Content-Disposition header suggests that browsers should display the PDF in a separate window or tab\nheader('Content-Disposition: attachment; filename="document.pdf"'); // Replace "document.pdf" with your file name\nheader("Pragma: public");\nheader("Expires: 0"); // Set expiration to 0 to prompt immediate downloads for HTTP/1.0 clients\nheader("Cache-Control: must-revalidate, post-check=0, pre-check=0"); // Force caches to obey the Expiration header\n\necho $pdfData;\nexit;